import { useState } from "react";
import UndoRedoControls from "./UndoRedoControls";

function App() {
  const [text, setText] = useState("");
  const [tone, setTone] = useState("formal");
  const [result, setResult] = useState("");
  const [history, setHistory] = useState([]);      // Stores past results
  const [redoStack, setRedoStack] = useState([]);  // Stores undone results

  const handleRewrite = async () => {
    try {
      // Save current result into history before changing
      if (result) {
        setHistory([...history, result]);
        setRedoStack([]); // Clear redo stack on new change
      }

      const res = await fetch("http://localhost:5000/api/rewrite", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ text, tone }),
      });
      const data = await res.json();
      setResult(data.output || JSON.stringify(data));
    } catch (err) {
      console.error(err);
      setResult("Error: Could not connect to backend");
    }
  };

  const handleUndo = () => {
    if (history.length === 0) return;
    const prev = history[history.length - 1];
    setRedoStack([result, ...redoStack]);
    setHistory(history.slice(0, -1));
    setResult(prev);
  };

  const handleRedo = () => {
    if (redoStack.length === 0) return;
    const next = redoStack[0];
    setHistory([...history, result]);
    setRedoStack(redoStack.slice(1));
    setResult(next);
  };

  const handleReset = () => {
    setHistory([]);
    setRedoStack([]);
    setResult("");
    setText("");
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-50">
      <div className="w-full max-w-lg p-6 bg-white rounded-2xl shadow-lg">
        <h1 className="text-2xl font-bold mb-4 text-center">Tone Picker</h1>

        <textarea
          value={text}
          onChange={(e) => setText(e.target.value)}
          placeholder="Enter text here"
          className="w-full p-3 border rounded-lg mb-4"
        />

        <div className="flex items-center gap-2 mb-4">
          <select
            value={tone}
            onChange={(e) => setTone(e.target.value)}
            className="flex-1 p-2 border rounded-lg"
          >
            <option value="formal">Formal</option>
            <option value="casual">Casual</option>
            <option value="friendly">Friendly</option>
          </select>

          <button
            onClick={handleRewrite}
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600"
          >
            Rewrite
          </button>
        </div>

        {/* Undo/Redo/Reset Buttons */}
        <UndoRedoControls
          onUndo={handleUndo}
          onRedo={handleRedo}
          onReset={handleReset}
          canUndo={history.length > 0}
          canRedo={redoStack.length > 0}
        />

        <div className="p-3 border rounded-lg bg-gray-100 mt-4">
          <strong>Result:</strong>
          <p>{result}</p>
        </div>
      </div>
    </div>
  );
}

export default App;
